using System;
using NSec.Cryptography;
using Xunit;

namespace NSec.Tests.Rfc
{
    public static class ChaCha20Poly1305Tests
    {
        public static readonly TheoryData<string[]> Rfc8439TestVectors = new TheoryData<string[]>
        {
            // Section 2.8.2
            new string[] { "4c616469657320616e642047656e746c656d656e206f662074686520636c617373206f66202739393a204966204920636f756c64206f6666657220796f75206f6e6c79206f6e652074697020666f7220746865206675747572652c2073756e73637265656e20776f756c642062652069742e", "50515253c0c1c2c3c4c5c6c7", "808182838485868788898a8b8c8d8e8f909192939495969798999a9b9c9d9e9f", "07000000" + "4041424344454647", "d31a8d34648e60db7b86afbc53ef7ec2a4aded51296e08fea9e2b5a736ee62d63dbea45e8ca9671282fafb69da92728b1a71de0a9e060b2905d6a5b67ecd3b3692ddbd7f2d778b8c9803aee328091b58fab324e4fad675945585808b4831d7bc3ff4def08e4b7a9de576d26586cec64b6116", "1ae10b594f09e26a7e902ecbd0600691" },
            // Appendix A.5
            new string[] { "", "f33388860000000000004e91", "1c9240a5eb55d38af333888604f6b5f0473917c1402b80099dca5cbc207075c0", "000000000102030405060708", "", "eead9d67890cbb22392336fea1851f38" },
        };

        public static readonly TheoryData<string[]> Rfc7634TestVectors = new TheoryData<string[]>
        {
            // Appendix A.
            new string[] { "45000054a6f200004001e778c6336405c000020508005b7a3a080000553bec100007362708090a0b0c0d0e0f101112131415161718191a1b1c1d1e1f202122232425262728292a2b2c2d2e2f303132333435363701020204", "0102030400000005", "808182838485868788898a8b8c8d8e8f909192939495969798999a9b9c9d9e9f", "a0a1a2a31011121314151617", "24039428b97f417e3c13753a4f05087b67c352e6a7fab1b982d466ef407ae5c614ee8099d52844eb61aa95dfab4c02f72aa71e7c4c4f64c9befe2facc638e8f3cbec163fac469b502773f6fb94e664da9165b82829f641e0", "76aaa8266b7fb0f7b11b369907e1ad43" },
            // Appendix B.
            new string[] { "0000000c000040010000000a00", "c0c1c2c3c4c5c6c7d0d1d2d3d4d5d6d72e202500000000090000004529000029", "808182838485868788898a8b8c8d8e8f909192939495969798999a9b9c9d9e9f", "a0a1a2a31011121314151617", "610394701f8d017f7c12924889", "6b71bfe25236efd7cdc67066906315b2" },
        };

        [Theory]
        [MemberData(nameof(Rfc8439TestVectors))]
        [MemberData(nameof(Rfc7634TestVectors))]
        public static void Test(string[] testVector)
        {
            var plaintext = testVector[0];
            var aad = testVector[1];
            var key = testVector[2];
            var nonce = testVector[3];
            var ciphertext = testVector[4];
            var tag = testVector[5];

            var a = AeadAlgorithm.ChaCha20Poly1305;

            using var k = Key.Import(a, key.DecodeHex(), KeyBlobFormat.RawSymmetricKey);

            var b = a.Encrypt(k, new Nonce(nonce.DecodeHex(), 0), aad.DecodeHex(), plaintext.DecodeHex());
            Assert.Equal((ciphertext + tag).DecodeHex(), b);

            Assert.True(a.Decrypt(k, new Nonce(nonce.DecodeHex(), 0), aad.DecodeHex(), b, out var r));
            Assert.Equal(plaintext.DecodeHex(), r);
        }
    }
}
